label("$-3$",(0,-3),W);
label("$3$",(0,3),W);
label("$7$",(0,7),W);
draw( (5,3) -- (-3,4) -- (-4,-4) -- (4,-5) -- cycle, red );
draw( (5,4) -- (-4,4) -- (-4,-5) -- (5,-5) -- cycle, dashed + blue );
