for (i=-7;i<8;++i) {
for (j=-7;j<8;++j) {
if (((-7 <= i) && (i <= -3)) || ((3 <= i) &&  (i<= 7))) { if (((-7 <= j) && (j <= -3)) || ((3 <= j) &&  (j<= 7))) { fill(shift(i,j)*p,black); }}}} draw((-7,-.2)--(-7,.2),black+0.5bp);
draw((-3,-.2)--(-3,.2),black+0.5bp);
draw((3,-.2)--(3,.2),black+0.5bp);
