draw( (5,3) -- (-3,4) -- (-4,-4) -- (4,-5) -- cycle, red );
draw( (5,-3) -- (-2,-5) -- (-4,2) -- (3,4) -- cycle, dashed + green );
draw( (5,-4) -- (-3,-5) -- (-4,3) -- (4,4) -- cycle, red );
draw( scale(1.05)*((5,4) -- (-4,4) -- (-4,-5) -- (5,-5) -- cycle), dashed + blue );
